  const cartContext=useContext(CartContext);

  const numberOfCartItems=cartContext
  return (
    <button className={classes.button} onClick={props.onPress}>
      <span className={classes.icon}>
        <CartIcon></CartIcon>
      </span>
      <span>Your Cart</span>
      <span className={classes.badge}>3</span>
    </button>
  );
};

